mapper.select:
select distinct sys_menu.id,sys_menu.menu_name,sys_menu.parent_id,sys_menu.order_num,sys_menu.path, sys_menu.component, sys_menu.`query`, sys_menu.route_name,sys_menu.is_frame, sys_menu.is_cache,sys_menu.menu_type,sys_menu.visible, sys_menu.status, ifnull(sys_menu.perms,'') as perms,sys_menu.icon from sys_menu left join sys_role_menu on sys_menu.id = sys_role_menu.menu_id left join sys_user_role on sys_role_menu.role_id = sys_user_role.role_id left join sys_role on sys_user_role.role_id = sys_role.id where sys_user_role.user_id = #{userId}
AND sys_menu.menu_name like concat('%', #{menuName}, '%')
AND sys_menu.visible = #{visible}
AND sys_menu.status = #{status}
order by sys_menu.parent_id, sys_menu.order_num

select sys_menu.id from sys_menu left join sys_role_menu on sys_menu.id = sys_role_menu.menu_id where sys_role_menu.role_id = #{roleId}
and sys_menu.id not in (select sys_menu.parent_id from sys_menu inner join sys_role_menu on sys_menu.id = sys_role_menu.menu_id and sys_role_menu.role_id = #{roleId})
order by sys_menu.parent_id, sys_menu.order_num
