mapper.select:
select sys_user.id, sys_user.dept_id, sys_user.nick_name, sys_user.user_name, sys_user.email, sys_user.phonenumber, sys_user.sex, sys_user.avatar, sys_user.status, sys_user.login_ip, sys_user.last_login_time, sys_user.create_user, sys_user.create_time, sys_user.remark, sys_dept.dept_name, sys_dept.leader from sys_user left join sys_dept on sys_user.dept_id = sys_dept.id where sys_user.delete_time = '1970-01-01 00:00:00'
AND sys_user.id = #{id}
AND sys_user.user_name like concat('%', #{userName}, '%')
AND sys_user.status = #{status}
AND sys_user.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(sys_user.create_time,'%Y%m%d') >= date_format(#{beginTime},'%Y%m%d')
AND date_format(sys_user.create_time,'%Y%m%d') <= date_format(#{endTime},'%Y%m%d')
AND (sys_user.dept_id = #{deptId} OR sys_user.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${dataScopeSql}

select distinct sys_user.id, sys_user.dept_id, sys_user.user_name, sys_user.nick_name, sys_user.email, sys_user.phonenumber, sys_user.status, sys_user.create_time from sys_user `user` left join sys_dept dept on sys_user.dept_id = sys_dept.id left join sys_user_role on sys_user.id = sys_user_role.user_id left join sys_role on sys_role.id = sys_user_role.role_id where sys_user.delete_time = '1970-01-01 00:00:00' and sys_role.id = #{roleId}
AND sys_user.user_name like concat('%', #{userName}, '%')
AND sys_user.phonenumber like concat('%', #{phonenumber}, '%')
${dataScopeSql}
